using System.Collections;using System.Collections.Generic;using UnityEngine;using Valve.VR.InteractionSystem;public class WhiteStickController : MonoBehaviour {    public SteamVR_TrackedController controller;    public SteamVR_LaserPointer laserPointer;    private void OnEnable()    {        laserPointer.PointerIn -= handlePointerIn;        laserPointer.PointerIn += handlePointerIn;    }    private void handlePointerIn(object sender, PointerEventArgs e)    {        TactileController tactileController = e.target.GetComponent<TactileController>();        if (tactileController != null && e.distance <= 1.6)        {            SteamVR_Controller.Input((int)controller.controllerIndex).TriggerHapticPulse(3900);        }                   }}